SELECT CONVERT(nvarchar(250),@@servername) AS 'ServerName\InstanceName',CONVERT(nvarchar(250),SERVERPROPERTY('servername')) AS 'ServerName',CONVERT(nvarchar(250),SERVERPROPERTY('machinename')) AS 'Windows_Name',CONVERT(nvarchar(250),SERVERPROPERTY('ComputerNamePhysicalNetBIOS')) AS 'NetBIOS_Name',CONVERT(nvarchar(250),ISNULL(SERVERPROPERTY('instanceName'),'DEFAULT')) AS 'InstanceName',CONVERT(nvarchar(250),SERVERPROPERTY('IsClustered')) AS 'IsClustered';